import Col from "react-bootstrap/Col";
import Form from "react-bootstrap/Form";
import Row from "react-bootstrap/Row";
import Button from "react-bootstrap/esm/Button";
import Card from "react-bootstrap/Card";
import "./Pedidos.css";
import { useEffect, useState } from "react";

function CrearPedidoForm(props) {
  const [tituloBoton, setTituloBoton] = useState(" + A単adir Pedido ");
  const [colorBoton, setColorBoton] = useState("success");
  const [dialogoCrearPedido, setDialogoCrearPedido] = useState(<div></div>);
  const [dialogoCrearPedidoDesplegado, setDialogoCrearPedidoDesplegado] =
    useState(false);

    if(props.actualizar){

    }
  useEffect(() => {
    console.log("Renderizado");
  });

  function addButtonPresionado() {
    if (dialogoCrearPedidoDesplegado) {
      setDialogoCrearPedidoDesplegado(false);
      setTituloBoton(" + A単adir Pedido ");
      setColorBoton("success");
      setDialogoCrearPedido(<div></div>);
    } else {
      setDialogoCrearPedidoDesplegado(true);
      setTituloBoton("Cancelar");
      setColorBoton("danger");
      setDialogoCrearPedido(<DialogoCrearPedido></DialogoCrearPedido>);
    }
  }

  return (
    <div className="crear-pedido-card">
      <div style={{ width: "100%" , textAlign:"center"}}>
          <Button size="lg" onClick={addButtonPresionado} variant={colorBoton} style={{marginTop:"1rem", marginBottom:"1rem"}}>
            {tituloBoton}
          </Button>
      </div>
      {dialogoCrearPedido}
    </div>
  );
}

function DialogoCrearPedido() {
  return (
    <div>
        <Card className="mx-auto" style={{ width: "80%"}}>
          <Card.Title style={{ textAlign: "center" }}>
            Crear nuevo pedido
          </Card.Title>
          <Card.Body>
            <Form>
              <Form.Group as={Row} className="mb-3" controlId="formNombre">
                <Form.Label column sm="2">
                  Nombre
                </Form.Label>
                <Col sm="10">
                  <Form.Control type="text" placeholder="Nombre pedido" />
                </Col>
              </Form.Group>
              <Form.Group as={Row} className="mb-3" controlId="formNit">
                <Form.Label column sm="2">
                  Nit
                </Form.Label>
                <Col sm="10">
                  <Form.Control type="text" placeholder="900645016" />
                </Col>
              </Form.Group>
              <Form.Group as={Row} className="mb-3" controlId="formCorreoContacto">
                <Form.Label column sm="2">
                  Correo Contacto
                </Form.Label>
                <Col sm="10">
                  <Form.Control type="text" placeholder="contacto@pedido.com" />
                </Col>
              </Form.Group>
              <div style={{ textAlign: "center" }}>
                <Button variant="primary" type="submit">
                  Crear
                </Button>
              </div>
            </Form>
          </Card.Body>
        </Card>
        <br></br>
    </div>
  );
  }

export default CrearPedidoForm;
